function removeAdjacentDuplicates(str) {
    let newStr = '';
    for (let i = 0; i < str.length; i++) {
        if (str[i] !== str[i + 1])
            if (str[i - 1] !== str[i])
                newStr += str[i];
    }
    return newStr;
}
function match(sentence, key_phrases) {
    for (let i = 0; i < sentence.length; i++) {
        for (let j = 0; j < key_phrases.length; j++) {
            const phrase = key_phrases[j];
            let matchFound = true;

            for (let k = 0; k < phrase.length; k++) {
                if (sentence[i + k] !== phrase[k]) {
                    matchFound = false;
                    break;
                }
            }

            if (matchFound) {
                return true;
            }
        }
    }

    return false;
}

function normalize(str) {
    return removeAdjacentDuplicates(
        str.toLowerCase()
    );
}

