// Helper functions
function removeAdjacentDuplicates(str) {
    let newStr = '';
    for (let i = 0; i < str.length; i++) {
        if (str[i] !== str[i + 1])
            newStr += str[i];
    }
    return newStr;
}
function match(sentence, key_phrases) {
    for (let i = 0; i < sentence.length; i++) {
        for (let j = 0; j < key_phrases.length; j++) {
            const phrase = key_phrases[j];
            let matchFound = true;

            for (let k = 0; k < phrase.length; k++) {
                if (sentence[i + k] !== phrase[k]) {
                    matchFound = false;
                    break;
                }
            }

            if (matchFound) {
                return true;
            }
        }
    }

    return false;
}

function normalize(str) {
    return removeAdjacentDuplicates(
        str.toLowerCase()
    );
}

function process_key_phrases(key_phrases) {
    var key_phrases_arrays = [];
    key_phrases.forEach(function (element) {
        var element_normalized = normalize(element);
        var element_splitted = element_normalized.split(/[^a-zA-Z]/);
        key_phrases_arrays.push(element_splitted);
    });
    return key_phrases_arrays;
}

